var socketAdmin,
    Q = require('q');
    
(function () {
    "use strict"
    
    var FoodLantern;
    
    socketAdmin = function (socket) {
        
        var self = this;
        
        this.socket = socket;
        
        this.initialize = function () {
            var deferred = Q.defer();
            
            FoodLantern = module.parent.parent.exports;
            
            deferred.resolve();
            
            return deferred.promise;
        };
        
        this.getGamesList = function () {
            self.socket.emit('gamesList', FoodLantern.games.listAll());
        };
        
        this.setupListeners = function () {
            var deferred = Q.defer();
            
            self.socket.on('gamesList', self.getGamesList);
            //self.socket.on('playersList', self.getPlayersList);
            //self.socket.on('lanternsList', self.getLanternsList);
            
            deferred.resolve();
            
            return deferred.promise;
        }
        
        this.initialize();
        
    };
    
}());

module.exports = socketAdmin;